mapper.select:
SELECT b.*,bt.book_type_name,u.real_name,IFNULL(e.evaluate_score,0) evaluate_score FROM tbook b join user u on b.user_id=u.user_id left join book_type bt on b.book_type_id=bt.book_type_id left join (select tbook_id,round(evaluate_score,2) evaluate_score from evaluate group by tbook_id) e on b.tbook_id=e.tbook_id
b.tbook_id = #{tbook_id}
b.tbook_author = #{tbook_author}

SELECT * FROM ( SELECT b.*,bt.book_type_name,u.real_name,IFNULL(e.evaluate_score,0) evaluate_score FROM tbook b join user u on b.user_id=u.user_id left join book_type bt on b.book_type_id=bt.book_type_id left join (select tbook_id,round(evaluate_score,2) evaluate_score from evaluate group by tbook_id) e on b.tbook_id=e.tbook_id
b.tbook_id = #{tbook_id}
and b.tbook_name like '%${tbook_name}%'
and b.user_id = #{user_id}
and u.real_name like '%${real_name}%'
and b.book_type_id = #{book_type_id}
and b.tbook_flag = #{tbook_flag}
order by IFNULL(e.evaluate_score,0) desc, tbook_click desc,b.tbook_id asc) t
order by tbook_click desc,b.tbook_id asc) t
order by IFNULL(e.evaluate_score,0) desc, tbook_date desc,b.tbook_id asc) t
order by tbook_date desc,b.tbook_id asc) t
Limit ${start},${limit}


SELECT count(*) FROM tbook b 
  join user u on b.user_id=u.user_id 
  left join book_type bt on b.book_type_id=bt.book_type_id 
  left join (select tbook_id,round(evaluate_score,2) evaluate_score from evaluate group by tbook_id) e 
    on b.tbook_id=e.tbook_id




	b.tbook_id = #{tbook_id}




	and b.tbook_name like '%${tbook_name}%'




	and b.user_id = #{user_id}




	and u.real_name like '%${real_name}%'




	and b.book_type_id = #{book_type_id}




	and b.tbook_flag = #{tbook_flag}
